import React from 'react';
import { Box, Text, Badge, VStack } from '@chakra-ui/react';

function ParkingSpot({ id, status }) {
  const statusColor = status === 'libre' ? 'green' : 'red';

  return (
    <Box
      p={4}
      borderWidth="1px"
      borderRadius="lg"
      bg="white"
      boxShadow="sm"
      _hover={{ boxShadow: 'md' }}
    >
      <VStack spacing={2}>
        <Text fontWeight="bold">Place {id}</Text>
        <Badge
          colorScheme={statusColor}
          px={2}
          py={1}
          borderRadius="full"
          fontSize="sm"
        >
          {status.toUpperCase()}
        </Badge>
      </VStack>
    </Box>
  );
}

export default ParkingSpot;
