import React from 'react';
import {
  Box,
  Text,
  VStack,
  HStack,
  Badge,
  useColorModeValue,
  useToast,
  Icon,
  Tooltip,
} from '@chakra-ui/react';
import { motion } from 'framer-motion';
import { FaCar, FaParking, FaClock } from 'react-icons/fa';

const MotionBox = motion(Box);

const ParkingSpot = ({ id, status, lastUpdate }) => {
  const toast = useToast();
  const bgColor = useColorModeValue('white', 'gray.800');
  const borderColor = useColorModeValue('gray.200', 'gray.700');
  const textColor = useColorModeValue('gray.600', 'gray.300');

  const getStatusColor = (status) => {
    switch (status) {
      case 'libre':
        return 'green';
      case 'occupé':
        return 'red';
      default:
        return 'gray';
    }
  };

  const getStatusIcon = (status) => {
    switch (status) {
      case 'libre':
        return FaParking;
      case 'occupé':
        return FaCar;
      default:
        return FaParking;
    }
  };

  const formatLastUpdate = (date) => {
    if (!date) return 'Jamais';
    const lastUpdate = new Date(date);
    const now = new Date();
    const diff = now - lastUpdate;
    const minutes = Math.floor(diff / 60000);

    if (minutes < 1) return 'À l\'instant';
    if (minutes < 60) return `Il y a ${minutes} minute${minutes > 1 ? 's' : ''}`;

    const hours = Math.floor(minutes / 60);
    if (hours < 24) return `Il y a ${hours} heure${hours > 1 ? 's' : ''}`;

    const days = Math.floor(hours / 24);
    return `Il y a ${days} jour${days > 1 ? 's' : ''}`;
  };

  return (
    <MotionBox
      initial={{ opacity: 0, y: 20 }}
      animate={{ opacity: 1, y: 0 }}
      exit={{ opacity: 0, y: -20 }}
      transition={{ duration: 0.3 }}
      p={5}
      bg={bgColor}
      borderRadius="xl"
      boxShadow="lg"
      borderWidth="1px"
      borderColor={borderColor}
      _hover={{ 
        transform: 'translateY(-2px)', 
        boxShadow: 'xl',
        borderColor: status === 'libre' ? 'green.300' : 'red.300'
      }}
    >
      <VStack spacing={4} align="stretch">
        <HStack justify="space-between" align="center">
          <HStack spacing={3}>
            <Icon 
              as={getStatusIcon(status)} 
              boxSize={6} 
              color={getStatusColor(status) + '.500'}
            />
            <Text fontSize="2xl" fontWeight="bold" color={textColor}>
              Place {id}
            </Text>
          </HStack>
          <Tooltip 
            label={status === 'libre' ? 'Place disponible' : 'Place occupée'} 
            placement="top"
          >
            <Badge
              colorScheme={getStatusColor(status)}
              px={3}
              py={1}
              borderRadius="full"
              fontSize="sm"
              textTransform="uppercase"
              letterSpacing="wider"
            >
              {status}
            </Badge>
          </Tooltip>
        </HStack>

        <HStack spacing={2} color={textColor}>
          <Icon as={FaClock} />
          <Text fontSize="sm">
            Dernière mise à jour : {formatLastUpdate(lastUpdate)}
          </Text>
        </HStack>

        <Box 
          h="2px" 
          bg={useColorModeValue('gray.100', 'gray.700')} 
          borderRadius="full" 
          my={2}
        />

        <HStack justify="space-between" color={textColor}>
          <Text fontSize="sm">ID: #{id.toString().padStart(2, '0')}</Text>
          <Text fontSize="sm">
            {status === 'libre' ? 'Prêt à accueillir un véhicule' : 'Véhicule en stationnement'}
          </Text>
        </HStack>
      </VStack>
    </MotionBox>
  );
};

export default ParkingSpot;
