import React, { useState } from 'react';
import axios from 'axios';
import { motion } from 'framer-motion';

const MotionDiv = motion.div;

const ParkingSpot = ({ id, status, lastUpdate }) => {
  const [isReserved, setIsReserved] = useState(false);

  const getStatusColor = (status) => {
    switch (status) {
      case 'libre':
        return 'bg-green-500';
      case 'occupé':
        return 'bg-red-500';
      case 'réservé':
        return 'bg-orange-500';
      default:
        return 'bg-gray-500';
    }
  };

  const formatLastUpdate = (date) => {
    if (!date) return 'Jamais';
    const lastUpdate = new Date(date);
    const now = new Date();
    const diff = now - lastUpdate;
    const minutes = Math.floor(diff / 60000);

    if (minutes < 1) return 'À l\'instant';
    if (minutes < 60) return `Il y a ${minutes} minute${minutes > 1 ? 's' : ''}`;

    const hours = Math.floor(minutes / 60);
    if (hours < 24) return `Il y a ${hours} heure${hours > 1 ? 's' : ''}`;

    const days = Math.floor(hours / 24);
    return `Il y a ${days} jour${days > 1 ? 's' : ''}`;
  };

  const handleReservation = async () => {
    if (status === 'libre' && !isReserved) {
      try {
        const response = await axios.post('http://192.168.1.114:3001/api/reserve-spot', { id });
        if (response.data) {
          setIsReserved(true);
          // Notification de succès
          alert(`La place ${id} a été réservée avec succès`);
        }
      } catch (error) {
        console.error('Erreur de réservation:', error);
        alert(error.response?.data?.error || 'Une erreur est survenue');
      }
    }
  };

  const handleCancelReservation = async () => {
    try {
      const response = await axios.post('http://192.168.1.114:3001/api/cancel-reservation', { id });
      if (response.data) {
        setIsReserved(false);
        // Notification de succès
        alert(`La réservation de la place ${id} a été annulée`);
      }
    } catch (error) {
      console.error('Erreur d\'annulation:', error);
      alert(error.response?.data?.error || 'Une erreur est survenue');
    }
  };

  return (
    <MotionDiv
      initial={{ opacity: 0, y: 20 }}
      animate={{ opacity: 1, y: 0 }}
      exit={{ opacity: 0, y: -20 }}
      transition={{ duration: 0.3 }}
      className="relative group"
    >
      <div className="bg-white dark:bg-gray-800 rounded-xl shadow-lg overflow-hidden transform transition-all duration-300 hover:scale-105 hover:shadow-xl">
        {/* En-tête avec gradient */}
        <div className="bg-gradient-to-r from-blue-500 to-purple-600 p-4">
          <div className="flex justify-between items-center">
            <h3 className="text-xl font-bold text-white">
              Place {id}
            </h3>
            <span className={`${getStatusColor(status)} text-white px-3 py-1 rounded-full text-sm font-medium`}>
              {status}
            </span>
          </div>
        </div>

        {/* Contenu */}
        <div className="p-4 space-y-4">
          {/* Dernière mise à jour */}
          <div className="flex items-center text-gray-600 dark:text-gray-300">
            <svg className="w-4 h-4 mr-2" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M12 8v4l3 3m6-3a9 9 0 11-18 0 9 9 0 0118 0z" />
            </svg>
            <span className="text-sm">{formatLastUpdate(lastUpdate)}</span>
          </div>

          {/* Bouton de réservation */}
          {isReserved ? (
            <button
              onClick={handleCancelReservation}
              className="w-full bg-red-500 hover:bg-red-600 text-white font-semibold py-2 px-4 rounded-lg transition-colors duration-300 flex items-center justify-center"
            >
              <svg className="w-5 h-5 mr-2" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M6 18L18 6M6 6l12 12" />
              </svg>
              Annuler la réservation
            </button>
          ) : (
            <button
              onClick={handleReservation}
              disabled={status === 'occupé' || status === 'réservé'}
              className={`w-full font-semibold py-2 px-4 rounded-lg transition-colors duration-300 flex items-center justify-center
                ${status === 'occupé' || status === 'réservé'
                  ? 'bg-gray-300 cursor-not-allowed'
                  : 'bg-blue-500 hover:bg-blue-600 text-white'
                }`}
            >
              <svg className="w-5 h-5 mr-2" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M12 6v6m0 0v6m0-6h6m-6 0H6" />
              </svg>
              Réserver
            </button>
          )}
        </div>

        {/* Indicateur de statut */}
        <div className={`absolute top-0 right-0 w-2 h-full ${getStatusColor(status)}`}></div>
      </div>
    </MotionDiv>
  );
};

export default ParkingSpot;
