import React, { useState } from 'react';
import { Box, Text, Badge, VStack, Button, useToast } from '@chakra-ui/react';
import { motion } from 'framer-motion';
import axios from 'axios';

const MotionBox = motion(Box);

function ParkingSpot({ id, status, lastUpdate }) {
  const [isReserved, setIsReserved] = useState(false);
  const toast = useToast();

  const getStatusColor = (status) => {
    switch (status) {
      case 'libre':
        return 'success.500';
      case 'occupé':
        return 'error.500';
      case 'réservé':
        return 'warning.500';
      default:
        return 'warning.500';
    }
  };

  const formatLastUpdate = (date) => {
    if (!date) return '';
    const updateDate = new Date(date);
    return updateDate.toLocaleTimeString();
  };

  const handleReservation = async () => {
    if (status === 'libre' && !isReserved) {
      try {
        const response = await axios.post('http://192.168.1.114:3001/api/reserve-spot', { id });
        if (response.data) {
          setIsReserved(true);
          toast({
            title: 'Réservation confirmée',
            description: `La place ${id} a été réservée avec succès`,
            status: 'success',
            duration: 3000,
            isClosable: true,
          });
        }
      } catch (error) {
        console.error('Erreur de réservation:', error);
        toast({
          title: 'Erreur de réservation',
          description: error.response?.data?.error || 'Une erreur est survenue',
          status: 'error',
          duration: 3000,
          isClosable: true,
        });
      }
    }
  };

  const handleCancelReservation = async () => {
    try {
      const response = await axios.post('http://192.168.1.114:3001/api/cancel-reservation', { id });
      if (response.data) {
        setIsReserved(false);
        toast({
          title: 'Réservation annulée',
          description: `La réservation de la place ${id} a été annulée`,
          status: 'warning',
          duration: 3000,
          isClosable: true,
        });
      }
    } catch (error) {
      console.error('Erreur d\'annulation:', error);
      toast({
        title: 'Erreur d\'annulation',
        description: error.response?.data?.error || 'Une erreur est survenue',
        status: 'error',
        duration: 3000,
        isClosable: true,
      });
    }
  };

  return (
    <MotionBox
      p={4}
      borderWidth="1px"
      borderRadius="lg"
      bg="white"
      boxShadow="sm"
      borderColor="border.200"
      _hover={{ boxShadow: 'md', borderColor: 'primary.500' }}
      initial={{ opacity: 0, y: 20 }}
      animate={{ opacity: 1, y: 0 }}
      transition={{ duration: 0.3 }}
      whileHover={{ scale: 1.02 }}
      whileTap={{ scale: 0.98 }}
    >
      <VStack spacing={3}>
        <Text 
          fontWeight="bold" 
          color="text.primary"
          fontSize="lg"
        >
          Place {id}
        </Text>
        <Badge
          bg={getStatusColor(status)}
          color="white"
          px={3}
          py={1}
          borderRadius="full"
          fontSize="sm"
          fontWeight="medium"
        >
          {status.toUpperCase()}
        </Badge>
        <Text 
          fontSize="xs" 
          color="text.secondary"
        >
          Dernière mise à jour: {formatLastUpdate(lastUpdate)}
        </Text>

        {isReserved ? (
          <Button
            colorScheme="error"
            bg="error.500"
            _hover={{ bg: 'error.600' }}
            size="sm"
            width="full"
            onClick={handleCancelReservation}
          >
            Annuler la réservation
          </Button>
        ) : (
          <Button
            colorScheme="primary"
            bg="primary.500"
            _hover={{ bg: 'primary.600' }}
            size="sm"
            width="full"
            onClick={handleReservation}
            isDisabled={status === 'occupé' || status === 'réservé'}
          >
            Réserver
          </Button>
        )}
      </VStack>
    </MotionBox>
  );
}

export default ParkingSpot;
