import React, { useState, useEffect, useCallback } from 'react';
import { ChakraProvider, Box, Heading, Container, SimpleGrid, useToast } from '@chakra-ui/react';
import ParkingSpot from './components/ParkingSpot';
import NotificationConfig from './components/NotificationConfig';

function App() {
  const [parkingSpots, setParkingSpots] = useState([]);
  const toast = useToast();

  // Fonction pour gérer les messages WebSocket
  const handleWebSocketMessage = useCallback((event) => {
    const message = JSON.parse(event.data);
    
    switch (message.type) {
      case 'INITIAL_STATE':
        setParkingSpots(message.data);
        break;
      case 'UPDATE':
        setParkingSpots(prevSpots => {
          // Trouver la place actuelle
          const currentSpot = prevSpots.find(spot => spot.id === message.data.id);
          
          // Vérifier si l'état a réellement changé
          if (currentSpot && currentSpot.status === message.data.status) {
            // Si l'état est le même, mettre à jour uniquement la date
            return prevSpots.map(spot => 
              spot.id === message.data.id 
                ? { ...spot, lastUpdate: message.data.lastUpdate }
                : spot
            );
          } else {
            // Si l'état a changé, afficher une notification
            toast({
              title: `Place ${message.data.id} mise à jour`,
              description: `État: ${message.data.status}`,
              status: message.data.status === 'libre' ? 'success' : 'warning',
              duration: 3000,
              isClosable: true,
            });
            
            // Mettre à jour la place avec le nouvel état
            return prevSpots.map(spot => 
              spot.id === message.data.id ? message.data : spot
            );
          }
        });
        break;
      default:
        console.log('Message non géré:', message);
    }
  }, [toast]);

  // Initialisation de la connexion WebSocket
  useEffect(() => {
    const websocket = new WebSocket('ws://localhost:3002');

    websocket.onopen = () => {
      console.log('Connecté au serveur WebSocket');
    };

    websocket.onmessage = handleWebSocketMessage;

    websocket.onerror = (error) => {
      console.error('Erreur WebSocket:', error);
    };

    websocket.onclose = () => {
      console.log('Déconnecté du serveur WebSocket');
    };

    // Nettoyage lors du démontage
    return () => {
      websocket.close();
    };
  }, [handleWebSocketMessage]);

  return (
    <ChakraProvider>
      <Box minH="100vh" bg="gray.50" py={8}>
        <Container maxW="container.xl">
          <Heading as="h1" textAlign="center" mb={8} color="blue.600">
            Parking Intelligent
          </Heading>
          
          <SimpleGrid columns={{ base: 1, md: 2 }} spacing={8}>
            <Box>
              <Heading as="h2" size="lg" mb={4}>
                Places de Parking
              </Heading>
              <SimpleGrid columns={{ base: 2, md: 3 }} spacing={4}>
                {parkingSpots.map((spot) => (
                  <ParkingSpot 
                    key={spot.id} 
                    id={spot.id} 
                    status={spot.status}
                    lastUpdate={spot.lastUpdate}
                  />
                ))}
              </SimpleGrid>
            </Box>
            
            <Box>
              <NotificationConfig />
            </Box>
          </SimpleGrid>
        </Container>
      </Box>
    </ChakraProvider>
  );
}

export default App;
