import React, { useState } from 'react';
import {
  Box,
  Text,
  VStack,
  HStack,
  Badge,
  Button,
  useColorModeValue,
  useToast,
  Image
} from '@chakra-ui/react';
import { motion } from 'framer-motion';
import axios from 'axios';

import libreImg from '../assets/libre.png';
import occupeImg from '../assets/occupe.png';
import reserveImg from '../assets/reserve.png';

const MotionBox = motion(Box);

const getImageByStatus = (status) => {
  switch (status) {
    case 'libre':
      return libreImg;
    case 'occupé':
      return occupeImg;
    case 'réservé':
      return reserveImg;
    default:
      return libreImg;
  }
};

const ParkingSpot = ({ id, status, lastUpdate }) => {
  const [isReserved, setIsReserved] = useState(false);
  const toast = useToast();
  const bgColor = useColorModeValue('background.100', 'gray.800');
  const borderColor = useColorModeValue('border.200', 'gray.700');

  const getStatusColor = (status) => {
    switch (status) {
      case 'libre':
        return 'primary';
      case 'occupé':
        return 'accent';
      case 'réservé':
        return 'primary';
      default:
        return 'gray';
    }
  };

  const formatLastUpdate = (date) => {
    if (!date) return 'Jamais';
    const lastUpdate = new Date(date);
    const now = new Date();
    const diff = now - lastUpdate;
    const minutes = Math.floor(diff / 60000);

    if (minutes < 1) return 'À l\'instant';
    if (minutes < 60) return `Il y a ${minutes} minute${minutes > 1 ? 's' : ''}`;

    const hours = Math.floor(minutes / 60);
    if (hours < 24) return `Il y a ${hours} heure${hours > 1 ? 's' : ''}`;

    const days = Math.floor(hours / 24);
    return `Il y a ${days} jour${days > 1 ? 's' : ''}`;
  };

  const handleReservation = async () => {
    if (status === 'libre' && !isReserved) {
      try {
        const response = await axios.post('http://192.168.1.114:3001/api/reserve-spot', { id });
        if (response.data) {
          setIsReserved(true);
          toast({
            title: 'Réservation confirmée',
            description: `La place ${id} a été réservée avec succès`,
            status: 'success',
            duration: 3000,
            isClosable: true,
          });
        }
      } catch (error) {
        console.error('Erreur de réservation:', error);
        toast({
          title: 'Erreur de réservation',
          description: error.response?.data?.error || 'Une erreur est survenue',
          status: 'error',
          duration: 3000,
          isClosable: true,
        });
      }
    }
  };

  const handleCancelReservation = async () => {
    try {
      const response = await axios.post('http://192.168.1.114:3001/api/cancel-reservation', { id });
      if (response.data) {
        setIsReserved(false);
        toast({
          title: 'Réservation annulée',
          description: `La réservation de la place ${id} a été annulée`,
          status: 'warning',
          duration: 3000,
          isClosable: true,
        });
      }
    } catch (error) {
      console.error('Erreur d\'annulation:', error);
      toast({
        title: 'Erreur d\'annulation',
        description: error.response?.data?.error || 'Une erreur est survenue',
        status: 'error',
        duration: 3000,
        isClosable: true,
      });
    }
  };

  return (
    <MotionBox
      initial={{ opacity: 0, y: 20 }}
      animate={{ opacity: 1, y: 0 }}
      exit={{ opacity: 0, y: -20 }}
      transition={{ duration: 0.3 }}
      p={4}
      bg={bgColor}
      borderRadius="lg"
      boxShadow="md"
      borderWidth="1px"
      borderColor={borderColor}
      _hover={{ transform: 'translateY(-2px)', boxShadow: 'lg' }}
    >
      <VStack spacing={3} align="stretch">
        <Image
          src={getImageByStatus(status)}
          alt={`Place ${id} - ${status}`}
          boxSize="80px"
          objectFit="contain"
          mx="auto"
        />
        <HStack justify="space-between">
          <Text fontSize="xl" fontWeight="bold">
            Place {id}
          </Text>
          <Badge
            colorScheme={getStatusColor(status)}
            px={2}
            py={1}
            borderRadius="full"
            fontSize="sm"
          >
            {status}
          </Badge>
        </HStack>

        <Text fontSize="sm" color="gray.500">
          Dernière mise à jour : {formatLastUpdate(lastUpdate)}
        </Text>

        {isReserved ? (
          <Button
            colorScheme="red"
            size="sm"
            onClick={handleCancelReservation}
            _hover={{ bg: 'red.600' }}
          >
            Annuler la réservation
          </Button>
        ) : (
          <Button
            colorScheme="blue"
            size="sm"
            onClick={handleReservation}
            isDisabled={status === 'occupé' || status === 'réservé'}
            _hover={{ bg: 'blue.600' }}
          >
            Réserver
          </Button>
        )}
      </VStack>
    </MotionBox>
  );
};

export default ParkingSpot;
