import React, { useState } from 'react';
import {
  Box,
  Text,
  VStack,
  HStack,
  Badge,
  Button,
  useColorModeValue,
  useToast,
} from '@chakra-ui/react';
import { motion } from 'framer-motion';
import axios from 'axios';
import { FaCheckCircle, FaTimesCircle, FaLock } from 'react-icons/fa';

const MotionBox = motion(Box);

const getStatusIcon = (status) => {
  switch (status) {
    case 'libre':
      return <span style={{ color: '#4ade80', fontSize: '2rem' }}>‚úì</span>;
    case 'r√©serv√©':
      return <span style={{ color: '#f59e0b', fontSize: '2rem' }}>üîí</span>;
    case 'occup√©':
      return <span style={{ color: '#ef4444', fontSize: '2rem' }}>‚õî</span>;
    default:
      return null;
  }
};

const getStatusColor = (status) => {
  switch (status) {
    case 'libre':
      return '#4ade80';
    case 'r√©serv√©':
      return '#f59e0b';
    case 'occup√©':
      return '#ef4444';
    default:
      return 'white';
  }
};

const ParkingSpot = ({ id, status, lastUpdate }) => {
  const [isReserved, setIsReserved] = useState(false);
  const toast = useToast();

  const formatLastUpdate = (date) => {
    if (!date) return 'Jamais';
    const lastUpdate = new Date(date);
    const now = new Date();
    const diff = now - lastUpdate;
    const minutes = Math.floor(diff / 60000);
    if (minutes < 1) return '√Ä l\'instant';
    if (minutes < 60) return `Il y a ${minutes} minute${minutes > 1 ? 's' : ''}`;
    const hours = Math.floor(minutes / 60);
    if (hours < 24) return `Il y a ${hours} heure${hours > 1 ? 's' : ''}`;
    const days = Math.floor(hours / 24);
    return `Il y a ${days} jour${days > 1 ? 's' : ''}`;
  };

  const handleReservation = async () => {
    if (status === 'libre' && !isReserved) {
      try {
        const response = await axios.post('http://192.168.1.114:3001/api/reserve-spot', { id });
        if (response.data) {
          setIsReserved(true);
          toast({
            title: 'R√©servation confirm√©e',
            description: `La place ${id} a √©t√© r√©serv√©e avec succ√®s`,
            status: 'success',
            duration: 4000,
            isClosable: true,
            position: 'bottom-right',
            containerStyle: {
              borderRadius: '0.5rem',
              background: '#166534',
              color: 'white',
              boxShadow: '0 4px 6px rgba(0,0,0,0.1)'
            }
          });
        }
      } catch (error) {
        console.error('Erreur de r√©servation:', error);
        toast({
          title: 'Erreur de r√©servation',
          description: error.response?.data?.error || 'Une erreur est survenue',
          status: 'error',
          duration: 4000,
          isClosable: true,
          position: 'bottom-right',
          containerStyle: {
            borderRadius: '0.5rem',
            background: '#b91c1c',
            color: 'white',
            boxShadow: '0 4px 6px rgba(0,0,0,0.1)'
          }
        });
      }
    }
  };

  const handleCancelReservation = async () => {
    try {
      const response = await axios.post('http://192.168.1.114:3001/api/cancel-reservation', { id });
      if (response.data) {
        setIsReserved(false);
        toast({
          title: 'R√©servation annul√©e',
          description: `La r√©servation de la place ${id} a √©t√© annul√©e`,
          status: 'warning',
          duration: 4000,
          isClosable: true,
          position: 'bottom-right',
          containerStyle: {
            borderRadius: '0.5rem',
            background: '#b45309',
            color: 'white',
            boxShadow: '0 4px 6px rgba(0,0,0,0.1)'
          }
        });
      }
    } catch (error) {
      console.error('Erreur d\'annulation:', error);
      toast({
        title: 'Erreur d\'annulation',
        description: error.response?.data?.error || 'Une erreur est survenue',
        status: 'error',
        duration: 4000,
        isClosable: true,
        position: 'bottom-right',
        containerStyle: {
          borderRadius: '0.5rem',
          background: '#b91c1c',
          color: 'white',
          boxShadow: '0 4px 6px rgba(0,0,0,0.1)'
        }
      });
    }
  };

  return (
    <MotionBox
      bg="#1e293b"
      borderRadius="1rem"
      boxShadow="0 4px 6px rgba(0,0,0,0.1)"
      p={6}
      display="flex"
      flexDirection="column"
      alignItems="center"
      transition="transform 0.2s"
      _hover={{ transform: 'translateY(-5px)' }}
      minH="260px"
    >
      {getStatusIcon(status)}
      <Text fontSize="1.5rem" fontWeight="600" mb={2} color="#f8fafc">
        Place {id}
      </Text>
      <Text fontSize="0.9rem" color="#94a3b8" mb={2} textAlign="center">
        Derni√®re mise √† jour : {formatLastUpdate(lastUpdate)}
      </Text>
      <Text
        fontWeight="bold"
        fontSize="1.2rem"
        mb={4}
        color={getStatusColor(status)}
      >
        {status.toUpperCase()}
      </Text>
      {isReserved ? (
        <Button
          bg="#ef4444"
          _hover={{ bg: "#dc2626" }}
          color="white"
          borderRadius="0.5rem"
          fontWeight="600"
          px={6}
          py={3}
          onClick={handleCancelReservation}
        >
          Annuler la r√©servation
        </Button>
      ) : (
        <Button
          bg="#3b82f6"
          _hover={{ bg: "#2563eb" }}
          color="white"
          borderRadius="0.5rem"
          fontWeight="600"
          px={6}
          py={3}
          onClick={handleReservation}
          isDisabled={status === 'occup√©' || status === 'r√©serv√©'}
        >
          R√©server
        </Button>
      )}
    </MotionBox>
  );
};

export default ParkingSpot;
