import React, { useState } from 'react';
import {
  Box,
  Heading,
  FormControl,
  FormLabel,
  Input,
  Button,
  VStack,
  useToast,
  Switch,
  Text,
  Icon,
  HStack,
  Divider,
  useColorModeValue,
} from '@chakra-ui/react';
import { motion } from 'framer-motion';
import { FaBell, FaEnvelope, FaCheck } from 'react-icons/fa';

const MotionBox = motion(Box);

function NotificationConfig() {
  const [email, setEmail] = useState('');
  const [isSubscribed, setIsSubscribed] = useState(false);
  const toast = useToast();

  const bgColor = useColorModeValue('white', 'gray.800');
  const borderColor = useColorModeValue('gray.200', 'gray.700');

  const handleSubmit = (e) => {
    e.preventDefault();
    toast({
      title: isSubscribed ? 'Désabonnement réussi' : 'Abonnement réussi',
      description: `Les notifications seront ${isSubscribed ? 'désactivées' : 'envoyées'} à ${email}`,
      status: 'success',
      duration: 5000,
      isClosable: true,
      position: 'top-right',
      icon: <Icon as={FaCheck} />,
    });
  };

  return (
    <MotionBox
      p={8}
      bg={bgColor}
      borderRadius="xl"
      boxShadow="lg"
      borderWidth="2px"
      borderColor={borderColor}
      initial={{ opacity: 0, y: 20 }}
      animate={{ opacity: 1, y: 0 }}
      transition={{ duration: 0.3 }}
      whileHover={{ 
        boxShadow: 'xl',
        borderColor: `${borderColor}dd`
      }}
    >
      <VStack spacing={6} align="stretch">
        <HStack spacing={4} align="center">
          <Icon as={FaBell} w={6} h={6} color="brand.500" />
          <Heading as="h2" size="lg" color="gray.700">
            Configuration des Notifications
          </Heading>
        </HStack>

        <Divider />

        <form onSubmit={handleSubmit}>
          <VStack spacing={6} align="stretch">
            <FormControl>
              <FormLabel fontWeight="medium" color="gray.700">
                Adresse Email
              </FormLabel>
              <Input
                type="email"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                placeholder="votre@email.com"
                size="lg"
                bg="gray.50"
                borderColor="gray.200"
                _hover={{ borderColor: 'brand.300' }}
                _focus={{ borderColor: 'brand.500', boxShadow: '0 0 0 1px var(--chakra-colors-brand-500)' }}
                isRequired
              />
            </FormControl>

            <FormControl display="flex" alignItems="center">
              <FormLabel mb="0" fontWeight="medium" color="gray.700">
                Activer les notifications
              </FormLabel>
              <Switch
                isChecked={isSubscribed}
                onChange={(e) => setIsSubscribed(e.target.checked)}
                colorScheme="brand"
                size="lg"
              />
            </FormControl>

            <Box bg="blue.50" p={4} borderRadius="md">
              <HStack spacing={3}>
                <Icon as={FaEnvelope} color="brand.500" />
                <Text fontSize="sm" color="gray.600">
                  Vous recevrez des notifications par email lorsque le statut des places de parking changera.
                </Text>
              </HStack>
            </Box>

            <Button
              type="submit"
              colorScheme="brand"
              size="lg"
              width="full"
              leftIcon={<Icon as={isSubscribed ? FaBell : FaCheck} />}
              _hover={{
                transform: 'translateY(-2px)',
                boxShadow: 'lg',
              }}
              _active={{
                transform: 'translateY(0)',
              }}
            >
              {isSubscribed ? 'Se désabonner' : 'S\'abonner'}
            </Button>
          </VStack>
        </form>
      </VStack>
    </MotionBox>
  );
}

export default NotificationConfig;
